mapper.select:
		SELECT emp_num,emp_name,emp_mail,emp_tel,emp_hire,c.code_name AS emp_dept,emp_manager,comments
		FROM employees e join common_code c
   		 ON e.emp_dept = c.code_id 


   		 	emp_num = #{empNum}


   		 	emp_name = #{empName}


            emp_dept = #{empDept}










   		ORDER BY emp_num
