mapper.insert:

	select nvl(max(order_no),0)+1 from m_order		

insert into m_order(
			order_no,
			order_date,
			comments,
			order_receiving,
			manager

	)
	values (
			#{orderNo}
			,#{orderDate}
			,#{comments}
			,#{orderReceiving}
			,#{manager}

	)


	select nvl(max(no),0)+1 from m_order_detail		

insert into m_order_detail (
			no,
			pro_code,
			pro_name,
			standard,
			sell_price,
			unit,
			order_num,

			sum_price			
			)
	values (
			 #{no}
			,#{proCode}
			,#{proName}
			,#{sellPrice}
			,#{standard}
			,#{unit}
			,#{orderNum}
			#{sumPrice}
			)
