mapper.insert:

	select nvl(max(order_no),0)+1 from m_order		

insert into m_order(
			order_no,
			order_date,

			comments,

			order_receiving,
			emp_num

	)
	values (
			#{orderNo}
			,#{orderDate}

			,#{comments}

			,#{orderReceiving}
			,#{empNum}

	)


	select nvl(max(no),0)+1 from m_order_detail		

insert into m_order_detail (
			no,
			order_no,
			pro_code,
			pro_name,
			standard,
			sell_price,
			unit,
			order_num,
			client_code,
			sum_price			
			)
	values (
			 #{no}
			,#{orderNo}
			,#{proCode}
			,#{proName}
			,#{standard, jdbcType=VARCHAR}
			,#{sellPrice}
			,#{unit, jdbcType=VARCHAR}
			,#{orderNum}
			,#{clientCode}
			,#{sumPrice}
			)
